document.addEventListener("DOMContentLoaded", () => {
    const letters = "ABCDEFGHIJKLMNOPQRSTUVWXYZ";
    const elements = document.getElementsByClassName("hackletters");

    for (let i = 0; i < elements.length; i++) {
        const element = elements[i];
        animateLetters(element, letters);
    }
});

function animateLetters(element, letters) {
    let iterations = 0;
    const interval = setInterval(() => {
        element.innerText = element.innerText.split("")
            .map((letter, index) => {
                if (index < iterations) {
                    return element.dataset.value[index];
                }
                return letters[Math.floor(Math.random() * 26)];
            })
            .join("");
        if (iterations >= element.dataset.length) {
            clearInterval(interval);
        }
        iterations += 1 / 5;
    }, 30);
}
